<?phpnamespace Library;use Composer\Installer\PackageEvent;use Composer\EventDispatcher\Event;class Tools{    public static function init (Event $event) {        AddEventHandler('iblock', 'OnIBlockPropertyBuildList', ['CustomPropertyColor', 'getDescription']);        AddEventHandler('main', 'OnUserTypeBuildList', ['CustomPropertyColor', 'getDescription']);    }    public static function prePackageInstallOrUpdate (PackageEvent  $event) {        if(PHP_SAPI == 'cli') {            return;        }        copy('../install/jscolor.min.js', '/bitrix/js/jscolor.ForBitrix/jscolor.min.js');        copy('../install/jscolor_install.js', '/bitrix/js/jscolor.ForBitrix/jscolor_install.js');    }    public static function prePackageUninstall(PackageEvent  $event) {        if(PHP_SAPI == 'cli') {            return;        }        unlink('/bitrix/js/jscolor.ForBitrix/jscolor.min.js');        unlink('/bitrix/js/jscolor.ForBitrix/jscolor_install.js');    }}